export default {
  run() {
    const emp = appsmith.store.currentEmployee;
    if (!emp || !emp.interface) { showAlert('Unauthorized', 'error'); return; }
    if (emp.interface === 'Bar') { navigateTo('Bar'); }
    else if (emp.interface === 'Restaurant') { navigateTo('Restaurant'); }
    else if (emp.interface === 'Reception') { navigateTo('Reception'); }
    else { navigateTo('Dashboard'); }
  }
};

export default {
  run() {
    const q = Prompt.text.toLowerCase();
    // simple routing by keywords
    if (q.includes('unpaid') && q.includes('bar')) {
      return ai_query.run({ kind: 'UNPAID_BAR_WEEK' });
    }
    if (q.includes('top') && q.includes('items')) {
      return ai_query.run({ kind: 'TOP_ITEMS' });
    }
    if (q.includes('sales') && q.includes('interface')) {
      return ai_query.run({ kind: 'SALES_BY_INTERFACE' });
    }
    // default
    return ai_query.run({ kind: 'SALES_BY_INTERFACE' });
  }
};

actionConfiguration.body:
{{(() => {
  const k = this.params.kind;
  if (k === 'UNPAID_BAR_WEEK') {
    return `SELECT o.id, o.created_at FROM orders o WHERE o.status='open' AND o.interface='Bar' AND o.created_at >= now() - interval '7 days' ORDER BY o.created_at DESC;`;
  }
  if (k === 'TOP_ITEMS') {
    return `SELECT it.name, SUM(oi.quantity) AS qty FROM order_items oi JOIN items it ON it.id = oi.item_id JOIN orders o ON o.id = oi.order_id WHERE o.status='paid' AND oi.is_cancelled = FALSE GROUP BY it.name ORDER BY qty DESC LIMIT 10;`;
  }
  if (k === 'SALES_BY_INTERFACE') {
    return `SELECT o.interface, SUM(t.amount)::numeric(10,2) AS total_sales FROM transactions t JOIN orders o ON o.id = t.order_id GROUP BY o.interface ORDER BY o.interface;`;
  }
  return `SELECT 1 as result`;
})()}}
